function getRowValue(table, row) {
	var targetColIdx = table.getColumnIndex("IPA Target");
	var actualColIdx = table.getColumnIndex("IPA Actual");
	if(targetColIdx < 0 || actualColIdx < 0) return false;



	var ipaTarget = row[targetColIdx];
	var ipaActual = row[actualColIdx];
	if(ipaTarget == null || ipaActual == null) return false;

	if("ˈoto" == ipaTarget.toString()) {
		java.lang.System.out.println("here");
	}

	if(diacriticOptions.ignoreDiacritics == true) {
		if("only" == diacriticOptions.selectionMode.toString().toLowerCase()) {
			ipaTarget = ipaTarget.stripDiacritics(diacriticOptions.selectedDiacritics);
			ipaActual = ipaActual.stripDiacritics(diacriticOptions.selectedDiacritics);
		} else {

			ipaTarget = ipaTarget.stripDiacriticsExcept(diacriticOptions.selectedDiacritics);
			ipaActual = ipaActual.stripDiacriticsExcept(diacriticOptions.selectedDiacritics);
		}
	}

	var accurate = ipaTarget.length() == ipaActual.length();
	if(accurate == true) {
		for(var pidx = 0; pidx < ipaTarget.length(); pidx++) {
			var ipaT = ipaTarget.elementAt(pidx);
			var ipaA = ipaActual.elementAt(pidx);

			accurate &= (ipaT.getText() == ipaA.getText() ||
				(ignoreDistortedDiacritic == true && ipaA.featureSet.hasFeature("distorted")));
		}
	}
	return accurate;
}

function run(context) {
	var table = context.get("table");

	table.setColumnTitle(table.columnCount, "Accurate");

	for(var row = 0; row < table.rowCount; row++) {
		var rowValue = getRowValue(table, table.getRow(row));
		table.setValueAt(row, "Accurate", rowValue);
	}
}

