function init(inputs, outputs) {
	inputs.add("summaryTable", "");
	inputs.add("table", "");

	outputs.add("table", "", false, DefaultTableDataSource);
	outputs.add("isAccurateAndConsistent", "", false, java.lang.Boolean);
	outputs.add("isInaccurateAndConsistent", "", false, java.lang.Boolean);
	outputs.add("hasAccurateAndInconsistent", "", false, java.lang.Boolean);
	outputs.add("isInaccurateAndInconsistent", "", false, java.lang.Boolean);
}

/*
Summary table columns:
	Orthography
	IPA Target
	Count
	A & C
	I & C
	A & I
	I & I
 */
function run(context) {
	var summaryTable = context.get("summaryTable");
	var table = context.get("table");

	// accurate is true if any of the rows are accurate
	var numAccurate = 0;
	var consistencyMap = new java.util.HashMap();
	for(var row = 0; row < table.rowCount; row++) {
		var ipaActual = table.getValueAt(row, "IPA Actual");
		if(diacriticOptions.ignoreDiacritics == true) {
			if("only" == diacriticOptions.selectionMode.toString().toLowerCase()) {
				ipaActual = ipaActual.stripDiacritics(diacriticOptions.selectedDiacritics);
			} else {
				ipaActual = ipaActual.stripDiacriticsExcept(diacriticOptions.selectedDiacritics);
			}
		}

		var cnt = consistencyMap.get(ipaActual) || 0;
		consistencyMap.put(ipaActual, ++cnt);

		var rowAccurate = table.getValueAt(row, "Accurate");
		if(rowAccurate == true) {
			++numAccurate;
		}
	}
	var accurate = numAccurate > 1;
	var consistent = (consistencyMap.size() == 1);
	var accuracy = numAccurate / table.rowCount;

	var rowData = [];
	var row = 0;
	rowData[row++] = table.getValueAt(0, "Orthography (Word)");
	rowData[row++] = table.getValueAt(0, "IPA Target");
	rowData[row++] = table.rowCount;
	rowData[row++] = java.lang.Float.valueOf(accuracy * 100.0);
	rowData[row++] = accurate && consistent;
	rowData[row++] = !accurate && consistent;
	rowData[row++] = accurate && !consistent;
	rowData[row++] = !accurate && !consistent;
	summaryTable.addRow(rowData);

	context.put("table", table);
	context.put("isAccurateAndConsistent", accurate && consistent);
	context.put("isInaccurateAndConsistent", !accurate && consistent);
	context.put("hasAccurateAndInconsistent", accurate && !consistent);
	context.put("isInaccurateAndInconsistent", !accurate && !consistent);
}

function init(inputs, outputs) {
	inputs.add("session", "");
	inputs.add("speakerSummaryTable", "");
	inputs.add("sessionSummaryTable", "");

	outputs.add("sessionSummaryTable", "", false, DefaultTableDataSource);
}

function run(context) {
	var session = context.get("session");
	var speakerSummaryTable = context.get("speakerSummaryTable");
	var sessionSummaryTable = context.get("sessionSummaryTable");

	var accuracyTotal = 0;
	var accAndCon = 0;
	var inaccAndCon = 0;
	var accAndIncon = 0;
	var inaccAndIncon = 0;
	for(var row = 0; row < sessionSummaryTable.rowCount; row++) {
		accuracyTotal += sessionSummaryTable.getValueAt(row, "Accuracy (%)");
		accAndCon += (sessionSummaryTable.getValueAt(row, "A & C") == true ? 1 : 0);
		inaccAndCon += (sessionSummaryTable.getValueAt(row, "I & C") == true ? 1 : 0);
		accAndIncon += (sessionSummaryTable.getValueAt(row, "A & I") == true ? 1 : 0);
		inaccAndIncon += (sessionSummaryTable.getValueAt(row, "I & I") == true ? 1 : 0);
	}

	var summaryRow = [];
	var col = 0;
	summaryRow[col++] = ""; // orthography
	summaryRow[col++] = ""; // ipa target
	summaryRow[col++] = ""; // count

	var accuracyAvg = java.lang.Float.valueOf(accuracyTotal / sessionSummaryTable.rowCount);
	summaryRow[col++] = accuracyAvg;

	var accAndConP = java.lang.Float.valueOf((accAndCon / sessionSummaryTable.rowCount) * 100.0);
	summaryRow[col++] = accAndConP;

	var inaccAndConP = java.lang.Float.valueOf((inaccAndCon / sessionSummaryTable.rowCount) * 100.0);
	summaryRow[col++] = inaccAndConP;

	var accAndInconP = java.lang.Float.valueOf((accAndIncon / sessionSummaryTable.rowCount) * 100.0);
	summaryRow[col++] = accAndInconP;

	var inaccAndInconP = java.lang.Float.valueOf((inaccAndIncon / sessionSummaryTable.rowCount) * 100.0);
	summaryRow[col++] = inaccAndInconP;

	sessionSummaryTable.addRow(summaryRow);

	var speakerSummaryRow = [];
	col = 0;
	speakerSummaryRow[col++] = session;
	speakerSummaryRow[col++] = sessionSummaryTable.rowCount - 1;
	speakerSummaryRow[col++] = accuracyAvg;
	speakerSummaryRow[col++] = accAndConP;
	speakerSummaryRow[col++] = inaccAndConP;
	speakerSummaryRow[col++] = accAndInconP;
	speakerSummaryRow[col++] = inaccAndInconP;
	speakerSummaryTable.addRow(speakerSummaryRow);
}
