function init(inputs, outputs) {
	inputs.add("exists", "", false, false);
	inputs.add("table", "", false, false);
	inputs.add("summaryTable", "", false, false);
	inputs.add("session", "", false, false);
	inputs.add("includeWordInitialOnsets", "", false, false);
	inputs.add("includeWordMedialOnsets", "", false, false);
	inputs.add("includeMedialCodaOnset", "", false, false);
	inputs.add("includeWordFinalCodas", "", false, false);
}

function run(context) {
	var includeWordInitialOnsets = context.get("includeWordInitialOnsets");
	var includeWordMedialOnsets = context.get("includeWordMedialOnsets");
	var includeMedialCodaOnset = context.get("includeMedialCodaOnset");
	var includeWordFinalCodas = context.get("includeWordFinalCodas");

	var exists = context.get("exists");
	var summaryTable = context.get("summaryTable");
	var table = context.get("table");
	var session = context.get("session");

	var rowData = [];
	var col = 0;
	rowData[col++] = session;
	if(exists == true) {
		var counts = []
		counts["all"] = 0;
		counts["OO Initial"] = 0;
		counts["OO Medial"] = 0;
		counts["CO Medial"] = 0;
		counts["CC Final"] = 0;

		var totals = [];
		totals["all"] = 0;
		totals["OO Initial"] = 0;
		totals["OO Medial"] = 0;
		totals["CO Medial"] = 0;
		totals["CC Final"] = 0;

		for(var row = 0; row < table.rowCount; row++) {
			++counts["all"];
			var ctx = table.getValueAt(row, "Context");
			++counts[ctx];

			totals["all"] += table.getValueAt(row, "Proximity");
			totals[ctx] += table.getValueAt(row, "Proximity");
		}

		rowData[col++] = counts["all"];
		rowData[col++] = (counts["all"] > 0 ? totals["all"]/counts["all"] : 0);
		if(includeWordInitialOnsets == true)
			rowData[col++] = (counts["OO Initial"] > 0 ? totals["OO Initial"]/counts["OO Initial"] : 0);
		if(includeWordMedialOnsets == true)
			rowData[col++] = (counts["OO Medial"] > 0 ? totals["OO Medial"]/counts["OO Medial"] : 0);
		if(includeMedialCodaOnset == true)
			rowData[col++] = (counts["CO Medial"] > 0 ? totals["CO Medial"]/counts["CO Medial"] : 0);
		if(includeWordFinalCodas == true)
			rowData[col++] = (counts["CC Final"] > 0 ? totals["CC Final"]/counts["CC Final"] : 0);					
	} else {
		rowData[col++] = 0;
		rowData[col++] = 0;
		rowData[col++] = 0;
		rowData[col++] = 0;
		rowData[col++] = 0;
	}
	summaryTable.addRow(rowData);
}

function init(inputs, outputs) {
	inputs.add("speaker", "", false, false);
	inputs.add("speakerSummary", "", false, false);
	inputs.add("globalSummary", "", false, false);
	inputs.add("includeWordInitialOnsets", "", false, false);
	inputs.add("includeWordMedialOnsets", "", false, false);
	inputs.add("includeMedialCodaOnset", "", false, false);
	inputs.add("includeWordFinalCodas", "", false, false);
}

function run(context) {
	var speaker = context.get("speaker");
	var speakerSummary = context.get("speakerSummary");
	var globalSummary = context.get("globalSummary");

	var includeWordInitialOnsets = context.get("includeWordInitialOnsets");
	var includeWordMedialOnsets = context.get("includeWordMedialOnsets");
	var includeMedialCodaOnset = context.get("includeMedialCodaOnset");
	var includeWordFinalCodas = context.get("includeWordFinalCodas");

	var rowData = [];
	var col = 0;
	rowData[col++] = speaker;
	rowData[col++] = speaker.role;

	var count = 0;
	var totals = [];
	totals["all"] = 0;
	totals["OO Initial"] = 0;
	totals["OO Medial"] = 0;
	totals["CO Medial"] = 0;
	totals["CC Final"] = 0;

	for(var row = 0; row < speakerSummary.rowCount; row++) {
		count += speakerSummary.getValueAt(row, "Cluster Count");
		totals["all"] += speakerSummary.getValueAt(row, "Proximity");
		if(includeWordInitialOnsets == true)
			totals["OO Initial"] += speakerSummary.getValueAt(row, "OO Initial");
		if(includeWordMedialOnsets == true)
			totals["OO Medial"] += speakerSummary.getValueAt(row, "OO Medial");
		if(includeMedialCodaOnset == true)
			totals["CO Medial"] += speakerSummary.getValueAt(row, "CO Medial");
		if(includeWordFinalCodas == true)
			totals["CC Final"] += speakerSummary.getValueAt(row, "CC Final");
	}

	rowData[col++] = count;
	rowData[col++] = (speakerSummary.rowCount > 0 ? 
			totals["all"]/speakerSummary.rowCount : 0);
	if(includeWordInitialOnsets == true)
		rowData[col++] = (speakerSummary.rowCount > 0 ? 
			totals["OO Initial"]/speakerSummary.rowCount : 0);
	if(includeWordMedialOnsets == true)
		rowData[col++] = (speakerSummary.rowCount > 0 ? 
			totals["OO Medial"]/speakerSummary.rowCount : 0);
	if(includeMedialCodaOnset == true)
		rowData[col++] = (speakerSummary.rowCount > 0 ? 
			totals["CO Medial"]/speakerSummary.rowCount : 0);
	if(includeWordFinalCodas == true)
		rowData[col++] = (speakerSummary.rowCount > 0 ? 
			totals["CC Final"]/speakerSummary.rowCount : 0);

	globalSummary.addRow(rowData);
}

function init(inputs, outputs) {
	inputs.add("table", "", false, false);
	inputs.add("speaker", "", false, false);
}

function run(context) {
	var table = context.get("table");
	var speaker = context.get("speaker");

	var row = [];
	row[0] = speaker;
	row[1] = speaker.role;
	row[2] = 0;
	row[3] = 0;
	row[4] = 0;
	row[5] = 0;
	row[6] = 0;
	row[7] = 0;
	row[8] = 0;

	table.addRow(row);
}
