// word-initial onsets
(?<^\s?)(\c:O<2>)(?>\v) ||
// word-medial onsets
(?<\v\s?)(\c:O<2>)(?>\v) ||
// word-medial coda+onset
(?<\v)(\c:C\s?\c:O)(?>\v) ||
// word-final codas
(?<\v)(\c:C<2>)$

