                                                                importClass(java.lang.Integer)
importClass(java.lang.Float)

function init(inputs, outputs) {
	inputs.add("speaker", "speaker", false, false);
	inputs.add("sessionPath", "session", false, false);
	inputs.add("resultTable", "", false, false);
	inputs.add("includePMLU", "", false, false);
	inputs.add("includeEPMLU", "", false, false);
}

function tableOp(context, table) {
	var speaker = context.get("speaker");
	var sessionPath = context.get("sessionPath");
	var resultTable = context.get("resultTable");
	var history = speaker.getExtension(Packages.ca.phon.project.ParticipantHistory);
	var includePMLU = context.get("includePMLU");
	var includeEPMLU = context.get("includeEPMLU");

	var idx = 0;
	var row = [];
	row[idx++] = sessionPath;
	row[idx++] = speaker.role;
	row[idx++] = history.getAgeForSession(sessionPath);

	if(includePMLU == true) {
		var pmlu = [0.0, 0.0, 0.0];

		for(var rIdx = 0; rIdx < resultTable.rowCount; rIdx++) {
			pmlu[0] += Float.parseFloat(resultTable.getValueAt(rIdx, "Target PMLU"));
			pmlu[1] += Float.parseFloat(resultTable.getValueAt(rIdx, "Actual PMLU"));
			pmlu[2] += Float.parseFloat(resultTable.getValueAt(rIdx, "PWP"));
		}

		if(resultTable.rowCount > 0) {
			pmlu = pmlu.map(function (v) { 
				return v / resultTable.rowCount;
			});
		}
		pmlu.forEach(function (v) { row[idx++] = Float.valueOf(v); });
	}

	if(includeEPMLU == true) {
		var epmlu = [0.0, 0.0, 0.0];

		for(var rIdx = 0; rIdx < resultTable.rowCount; rIdx++) {
			epmlu[0] += Float.parseFloat(resultTable.getValueAt(rIdx, "Target ePMLU"));
			epmlu[1] += Float.parseFloat(resultTable.getValueAt(rIdx, "Actual ePMLU"));
			epmlu[2] += Float.parseFloat(resultTable.getValueAt(rIdx, "ePWP"));
		}

		if(resultTable.rowCount > 0) {
			epmlu = epmlu.map(function (v) { 
				return v / resultTable.rowCount;
			});
		}
		epmlu.forEach(function (v) { row[idx++] = Float.valueOf(v); });
	}

	table.addRow(row);
}

                                        importClass(java.lang.Float)

function init(inputs, outputs) {
	inputs.add("speaker", "", false, false);
	inputs.add("globalSummary", "", false, false);
	inputs.add("summary", "", false, false);
	inputs.add("includePMLU", "", false, false);
	inputs.add("includeEPMLU", "", false, false);

	outputs.add("table", "", false, Packages.ca.phon.query.report.datasource.DefaultTableDataSource);
}

function run(context) {
	var speaker = context.get("speaker");
	var globalSummary = context.get("globalSummary");
	var summary = context.get("summary");
	var includePMLU = context.get("includePMLU");
	var includeEPMLU = context.get("includeEPMLU");

	var idx = 0;
	var rowData = [];
	rowData[idx++] = speaker;
	rowData[idx++] = speaker.role;

	if(includePMLU == true) {
		var pmlu = [0.0, 0.0, 0.0];
		for(var rowIdx = 0; rowIdx < summary.rowCount; rowIdx++) {
			pmlu[0] += summary.getValueAt(rowIdx, "Target PMLU").floatValue();
			pmlu[1] += summary.getValueAt(rowIdx, "Actual PMLU").floatValue();
			pmlu[2] += summary.getValueAt(rowIdx, "PWP").floatValue();
		}
		if(summary.rowCount > 0) {
			pmlu = pmlu.map(function (v) { return v / summary.rowCount; });
		}
		pmlu.forEach(function (v) { rowData[idx++] = Float.valueOf(v); });
	}

	if(includeEPMLU == true) {
		var epmlu = [0.0, 0.0, 0.0];
		for(var rowIdx = 0; rowIdx < summary.rowCount; rowIdx++) {
			epmlu[0] += summary.getValueAt(rowIdx, "Target ePMLU").floatValue();
			epmlu[1] += summary.getValueAt(rowIdx, "Actual ePMLU").floatValue();
			epmlu[2] += summary.getValueAt(rowIdx, "ePWP").floatValue();
		}
		if(summary.rowCount > 0) {
			epmlu = epmlu.map(function (v) { return v / summary.rowCount; });
		}
		epmlu.forEach(function (v) { rowData[idx++] = Float.valueOf(v); });
	}

	globalSummary.addRow(rowData);
	context.put("table", globalSummary);
}
