function init(inputs, outputs) {
	inputs.add("speaker", "speaker", false, false);
	inputs.add("sessionPath", "session", false, false);
	inputs.add("accuracyTable", "", false, false);
}

function tableOp(context, table) {
	var row = [];

	var speaker = context.get("speaker");
	var sessionPath = context.get("sessionPath");
	var accuracyTable = context.get("accuracyTable");

	var history = speaker.getExtension(Packages.ca.phon.project.ParticipantHistory);

	var totalTargets = 0;
	var totalAccurate = 0;
	var totalSubstituted = 0;
	var totalDeleted = 0;

	for(var i = 0; i < accuracyTable.rowCount; i++) {
		totalTargets += accuracyTable.getValueAt(i, "Count").intValue();
		totalAccurate += accuracyTable.getValueAt(i, "Accurate").intValue();
		totalSubstituted += accuracyTable.getValueAt(i, "Substituted").intValue();
		totalDeleted += accuracyTable.getValueAt(i, "Deleted").intValue();
	}

	row[0] = sessionPath;
	row[1] = speaker.role;
	row[2] = history.getAgeForSession(sessionPath);
	row[3] = new java.lang.Integer(accuracyTable.rowCount);
	row[4] = new java.lang.Integer(totalTargets);
	row[5] = new java.lang.Integer(totalAccurate);
	row[6] = new java.lang.Integer(totalSubstituted);
	row[7] = new java.lang.Integer(totalDeleted);

	table.addRow(row);
}
