 */

 var compTypesParamInfo = {
	"id":["includeExactMatch", "includeCVPatternMatch", "includeSyllableCountMatch", "includeStressPatternMatch"],
	"title": "Comparators:",
	"desc":["Exact match", "CV pattern (see options below)", "Syllable Count", "Stress pattern"],
