	compTypesParam = new MultiboolScriptParam(
		compTypesParamInfo.id,
		compTypesParamInfo.def,
		compTypesParamInfo.desc,
		compTypesParamInfo.title,
		compTypesParamInfo.numCols);
	params.add(compTypesParam);
}

